mapper.update:
UPDATE access_points
SET url = #{url}, userId = #{userId}, name = #{name}, fileId = #{fileId}, description = #{description}, contactEmail = #{contactEmail}, technicalUrl = #{technicalUrl}
WHERE id = #{id}

UPDATE access_points

SET
usedForGalaxyGateway = #{usedForGalaxyGateway}
WHERE id = #{id}

mapper.delete:
DELETE FROM access_points
WHERE
id = #{id} AND userId = #{userId}
